import React from 'react';
import './ProfileCard.css';

const ProfileCard = ({ profile }) => {
  // Преобразуем строку интересов в массив, если она задана
  const interestsArray = profile.interests ? 
    profile.interests.split(',').map(interest => interest.trim()) : 
    [];

  // Формируем полный URL для фотографии
  const photoUrl = profile.photo ? 
    `http://localhost:5000${profile.photo.replace('/api', '')}` : 
    null;

  console.log('Photo URL:', photoUrl); // Добавляем для отладки

  return (
    <div className="profile-card">
      <div className="profile-image">
        {photoUrl ? (
          <img 
            src={photoUrl} 
            alt={profile.name} 
            onError={(e) => {
              console.error('Ошибка загрузки изображения:', e);
              e.target.src = 'https://via.placeholder.com/200x200?text=Нет+фото';
            }}
          />
        ) : (
          <div className="placeholder-image">Фото не загружено</div>
        )}
      </div>
      <div className="profile-info">
        <h2>{profile.name || 'Имя не указано'}</h2>
        <p className="age">{profile.age ? `${profile.age} лет` : 'Возраст не указан'}</p>
        <p className="city">{profile.city || 'Город не указан'}</p>
        <div className="additional-info">
          <p>{profile.about || 'Информация о себе не указана'}</p>
          <div className="interests">
            {interestsArray.length > 0 ? (
              interestsArray.map((interest, index) => (
                <span key={index} className="interest-tag">{interest}</span>
              ))
            ) : (
              <p>Интересы не указаны</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfileCard;
