import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { getApiUrl, getMediaUrl } from '../config/api';
import './AdminSettingsPage.css';

const AdminSettingsPage = () => {
  const [settings, setSettings] = useState({
    mainTitle: '',
    subTitle: '',
    description: '',
    headerBackground: 'color',
    headerBackgroundColor: '#ffffff',
    headerBackgroundImage: '',
    siteBackground: 'color',
    siteBackgroundColor: '#f5f5f5',
    siteBackgroundImage: '',
    telegramLink: '',
    whatsappLink: '',
    vkLink: '',
    profileTelegramLink: '',
    profileWhatsappLink: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    fetchSettings();
  }, []);

  const fetchSettings = async () => {
    try {
      const response = await axios.get(getApiUrl('/api/site-settings'));
      setSettings({
        ...settings,
        ...response.data,
        headerBackground: response.data.headerBackground || 'color',
        siteBackground: response.data.siteBackground || 'color',
        headerBackgroundColor: response.data.headerBackgroundColor || '#ffffff',
        siteBackgroundColor: response.data.siteBackgroundColor || '#f5f5f5',
        headerBackgroundImage: response.data.headerBackgroundImage || '',
        siteBackgroundImage: response.data.siteBackgroundImage || ''
      });
      setLoading(false);
    } catch (error) {
      console.error('Ошибка при загрузке настроек:', error);
      setError('Ошибка при загрузке настроек');
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError('');

    try {
      // Создаем объект с данными настроек
      const settingsData = {
        mainTitle: settings.mainTitle || '',
        subTitle: settings.subTitle || '',
        description: settings.description || '',
        headerBackground: settings.headerBackground || 'color',
        headerBackgroundColor: settings.headerBackgroundColor || '#ffffff',
        headerBackgroundImage: settings.headerBackgroundImage || '',
        siteBackground: settings.siteBackground || 'color',
        siteBackgroundColor: settings.siteBackgroundColor || '#f5f5f5',
        siteBackgroundImage: settings.siteBackgroundImage || '',
        telegramLink: settings.telegramLink || '',
        whatsappLink: settings.whatsappLink || '',
        vkLink: settings.vkLink || '',
        profileTelegramLink: settings.profileTelegramLink || '',
        profileWhatsappLink: settings.profileWhatsappLink || ''
      };

      console.log('Отправка настроек:', settingsData);

      const response = await axios.put(
        getApiUrl('/api/site-settings'),
        settingsData,
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        }
      );

      console.log('Ответ сервера:', response.data);
      
      // Обновляем состояние из ответа сервера
      setSettings(prev => ({
        ...prev,
        ...response.data,
        // Гарантируем, что значения не будут null
        headerBackground: response.data.headerBackground || prev.headerBackground,
        siteBackground: response.data.siteBackground || prev.siteBackground,
        headerBackgroundColor: response.data.headerBackgroundColor || prev.headerBackgroundColor,
        siteBackgroundColor: response.data.siteBackgroundColor || prev.siteBackgroundColor,
        headerBackgroundImage: response.data.headerBackgroundImage || prev.headerBackgroundImage,
        siteBackgroundImage: response.data.siteBackgroundImage || prev.siteBackgroundImage
      }));

      // Отправляем событие обновления фона
      window.dispatchEvent(new Event('settingsUpdated'));
      
      alert('Настройки успешно сохранены');
    } catch (error) {
      console.error('Ошибка при сохранении настроек:', error);
      setError(error.response?.data?.message || 'Ошибка при сохранении настроек');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleBackgroundUpload = async (e, type) => {
    const file = e.target.files[0];
    if (!file) return;

    try {
      const formData = new FormData();
      formData.append(type === 'header' ? 'headerBackgroundImage' : 'siteBackgroundImage', file);

      console.log('Загрузка изображения:', {
        type,
        fileName: file.name,
        fileSize: file.size,
        fileType: file.type
      });

      const response = await axios.post(
        getApiUrl(`/api/site-settings/${type === 'header' ? 'background' : 'image'}`),
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        }
      );

      console.log('Ответ сервера:', response.data);

      if (response.data.success) {
        setSettings(prev => ({
          ...prev,
          [type === 'header' ? 'headerBackgroundImage' : 'siteBackgroundImage']: response.data.path
        }));
      }
    } catch (error) {
      console.error('Ошибка при загрузке изображения:', error);
      setError('Ошибка при загрузке изображения');
    }
  };

  if (loading) return <div className="loading">Загрузка...</div>;

  return (
    <div className="admin-settings-page">
      <h1>Настройки сайта</h1>
      
      {error && <div className="error-message">{error}</div>}
      
      <form onSubmit={handleSubmit}>
        <div className="settings-section">
          <h2>Основные настройки</h2>
          
          <div className="form-group">
            <label>Заголовок сайта</label>
            <input
              type="text"
              value={settings.mainTitle}
              onChange={(e) => setSettings({...settings, mainTitle: e.target.value})}
            />
          </div>

          <div className="form-group">
            <label>Подзаголовок</label>
            <input
              type="text"
              value={settings.subTitle}
              onChange={(e) => setSettings({...settings, subTitle: e.target.value})}
            />
          </div>

          <div className="form-group">
            <label>Описание</label>
            <textarea
              value={settings.description}
              onChange={(e) => setSettings({...settings, description: e.target.value})}
              rows="4"
            />
          </div>
        </div>

        <div className="settings-section">
          <h2>Настройки фона</h2>
          
          <div className="form-group">
            <label>Фон шапки сайта</label>
            <div className="background-settings">
              <select
                value={settings.headerBackground || 'color'}
                onChange={(e) => setSettings({...settings, headerBackground: e.target.value})}
              >
                <option value="color">Цвет</option>
                <option value="image">Изображение</option>
              </select>
              
              {(settings.headerBackground || 'color') === 'color' ? (
                <input
                  type="color"
                  value={settings.headerBackgroundColor || '#ffffff'}
                  onChange={(e) => setSettings({...settings, headerBackgroundColor: e.target.value})}
                />
              ) : (
                <div className="image-upload">
                  <input
                    type="file"
                    accept="image/*"
                    onChange={(e) => handleBackgroundUpload(e, 'header')}
                  />
                  {settings.headerBackgroundImage && (
                    <img 
                      src={getMediaUrl(settings.headerBackgroundImage)} 
                      alt="Фон шапки" 
                      className="preview-image"
                    />
                  )}
                </div>
              )}
            </div>
          </div>

          <div className="form-group">
            <label>Фон сайта</label>
            <div className="background-settings">
              <select
                value={settings.siteBackground || 'color'}
                onChange={(e) => setSettings({...settings, siteBackground: e.target.value})}
              >
                <option value="color">Цвет</option>
                <option value="image">Изображение</option>
              </select>
              
              {(settings.siteBackground || 'color') === 'color' ? (
                <input
                  type="color"
                  value={settings.siteBackgroundColor || '#f5f5f5'}
                  onChange={(e) => setSettings({...settings, siteBackgroundColor: e.target.value})}
                />
              ) : (
                <div className="image-upload">
                  <input
                    type="file"
                    accept="image/*"
                    onChange={(e) => handleBackgroundUpload(e, 'site')}
                  />
                  {settings.siteBackgroundImage && (
                    <img 
                      src={getMediaUrl(settings.siteBackgroundImage)} 
                      alt="Фон сайта" 
                      className="preview-image"
                    />
                  )}
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="settings-section">
          <h2>Социальные сети</h2>
          
          <div className="form-group">
            <label>Ссылка на Telegram</label>
            <input
              type="text"
              value={settings.telegramLink}
              onChange={(e) => setSettings({...settings, telegramLink: e.target.value})}
            />
          </div>

          <div className="form-group">
            <label>Ссылка на WhatsApp</label>
            <input
              type="text"
              value={settings.whatsappLink}
              onChange={(e) => setSettings({...settings, whatsappLink: e.target.value})}
            />
          </div>

          <div className="form-group">
            <label>Ссылка на VK</label>
            <input
              type="text"
              value={settings.vkLink}
              onChange={(e) => setSettings({...settings, vkLink: e.target.value})}
            />
          </div>
        </div>

        <div className="settings-section">
          <h2>Настройки профилей</h2>
          
          <div className="form-group">
            <label>Шаблон ссылки Telegram для профилей</label>
            <input
              type="text"
              value={settings.profileTelegramLink}
              onChange={(e) => setSettings({...settings, profileTelegramLink: e.target.value})}
            />
          </div>

          <div className="form-group">
            <label>Шаблон ссылки WhatsApp для профилей</label>
            <input
              type="text"
              value={settings.profileWhatsappLink}
              onChange={(e) => setSettings({...settings, profileWhatsappLink: e.target.value})}
            />
          </div>
        </div>

        <div className="form-actions">
          <button type="submit" disabled={isSubmitting}>
            {isSubmitting ? 'Сохранение...' : 'Сохранить настройки'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AdminSettingsPage;
