import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProfileCard from '../components/ProfileCard';
import FilterSidebar from '../components/FilterSidebar';
import useGeolocation from '../hooks/useGeolocation';
import './ProfilesPage.css';

const ProfilesPage = () => {
  const [profiles, setProfiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [filters, setFilters] = useState({
    ageRange: '',
    gender: 'all',
    maritalStatus: 'all'
  });

  const { location, error: locationError } = useGeolocation();

  useEffect(() => {
    if (location) {
      fetchProfiles();
    }
  }, [location]);

  const fetchProfiles = async () => {
    try {
      // Отправляем координаты пользователя для получения анкет из его города
      const response = await axios.get('http://localhost:5000/api/public/profiles', {
        params: {
          latitude: location?.latitude,
          longitude: location?.longitude,
          city: location?.city
        }
      });
      console.log('Загруженные анкеты:', response.data);
      setProfiles(response.data);
    } catch (error) {
      console.error('Ошибка при загрузке анкет:', error);
      setError('Ошибка при загрузке анкет');
    } finally {
      setLoading(false);
    }
  };

  const filteredProfiles = profiles.filter(profile => {
    if (filters.ageRange && filters.ageRange !== 'all') {
      const [min, max] = filters.ageRange.split('-').map(Number);
      if (profile.age < min || (max && profile.age > max)) {
        return false;
      }
    }

    if (filters.gender !== 'all' && profile.gender !== filters.gender) {
      return false;
    }

    if (filters.maritalStatus !== 'all' && profile.maritalStatus !== filters.maritalStatus) {
      return false;
    }
    
    return true;
  });

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error || locationError) return <div className="error-message">{error || 'Ошибка определения местоположения'}</div>;

  return (
    <div className="profiles-page">
      <div className="profiles-header">
        <h1>Анкеты{location?.city ? ` в городе ${location.city}` : ''}</h1>
        <button 
          className="hamburger-button"
          onClick={() => setIsFilterOpen(true)}
        >
          <span className="hamburger-icon">☰</span>
        </button>
      </div>

      <FilterSidebar
        isOpen={isFilterOpen}
        onClose={() => setIsFilterOpen(false)}
        filters={filters}
        setFilters={setFilters}
      />
      
      {filteredProfiles.length === 0 ? (
        <div className="no-profiles">
          <p>Анкеты не найдены</p>
        </div>
      ) : (
        <div className="profiles-grid">
          {filteredProfiles.map(profile => (
            <ProfileCard key={profile._id} profile={profile} />
          ))}
        </div>
      )}
    </div>
  );
};

export default ProfilesPage;
