import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './CreateProfilePage.css';

const AdminCreateProfilePage = () => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState({
    name: '',
    age: '',
    city: '',
    gender: '',
    about: '',
    interests: '',
    photo: null,
    status: 'pending'
  });
  const [photoFile, setPhotoFile] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const resetForm = () => {
    setProfile({
      name: '',
      age: '',
      city: '',
      gender: '',
      about: '',
      interests: '',
      photo: null,
      status: 'pending'
    });
    setPhotoFile(null);
    setError('');
    if (document.getElementById('photo-input')) {
      document.getElementById('photo-input').value = '';
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Необходима авторизация');
        navigate('/login');
        return;
      }

      // Валидация данных перед отправкой
      if (!profile.name || !profile.age || !profile.city || !profile.gender) {
        setError('Пожалуйста, заполните все обязательные поля');
        return;
      }

      const age = parseInt(profile.age);
      if (isNaN(age) || age < 18 || age > 100) {
        setError('Возраст должен быть от 18 до 100 лет');
        return;
      }

      const formData = new FormData();
      Object.keys(profile).forEach(key => {
        if (key !== 'photo') {
          formData.append(key, profile[key]);
        }
      });
      
      if (photoFile) {
        // Проверка размера файла
        if (photoFile.size > 50 * 1024 * 1024) {
          setError('Размер файла не должен превышать 50 МБ');
          return;
        }

        // Проверка типа файла
        const allowedTypes = ['image/jpeg', 'image/png', 'image/gif', 'image/webp'];
        if (!allowedTypes.includes(photoFile.type)) {
          setError('Разрешены только изображения форматов JPEG, PNG, GIF и WebP');
          return;
        }

        console.log('Добавляем фото в FormData:', {
          name: photoFile.name,
          size: photoFile.size,
          type: photoFile.type
        });
        formData.append('photo', photoFile);
      }

      console.log('Отправляем запрос на создание анкеты...');
      const response = await axios.post(
        'http://localhost:5000/api/profiles',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          },
          timeout: 10000 // Таймаут 10 секунд
        }
      );

      console.log('Ответ сервера:', response.data);
      resetForm();
      navigate('/admin/profiles');
    } catch (error) {
      console.error('Ошибка при создании анкеты:', error);
      
      if (error.code === 'ECONNABORTED') {
        setError('Превышено время ожидания ответа от сервера. Попробуйте еще раз.');
      } else if (error.response) {
        // Ошибка от сервера
        setError(error.response.data.message || 'Ошибка при создании анкеты');
        if (error.response.data.errors) {
          setError(error.response.data.errors.join(', '));
        }
      } else if (error.request) {
        // Ошибка сети
        setError('Ошибка сети. Проверьте подключение к интернету.');
      } else {
        setError('Произошла ошибка при создании анкеты');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      console.log('Выбран файл:', {
        name: file.name,
        size: `${(file.size / 1024 / 1024).toFixed(2)} МБ`,
        type: file.type
      });

      // Проверяем размер файла (максимум 50 МБ)
      if (file.size > 50 * 1024 * 1024) {
        setError('Размер файла не должен превышать 50 МБ');
        e.target.value = '';
        return;
      }

      // Проверяем тип файла
      const allowedTypes = ['image/jpeg', 'image/png', 'image/gif', 'image/webp'];
      if (!allowedTypes.includes(file.type)) {
        setError('Разрешены только изображения форматов JPEG, PNG, GIF и WebP');
        e.target.value = '';
        return;
      }

      setPhotoFile(file);
      setError('');
    }
  };

  return (
    <div className="create-profile-page">
      <div className="create-profile-container">
        <h1>Создание новой анкеты</h1>
        
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSubmit} encType="multipart/form-data">
          <div className="form-group">
            <label>Фото</label>
            <div 
              className="photo-upload"
              onClick={() => document.getElementById('photo-input').click()}
            >
              {photoFile ? (
                <div className="photo-preview-container">
                  <img 
                    src={URL.createObjectURL(photoFile)} 
                    alt="Preview" 
                    className="photo-preview" 
                  />
                  <button 
                    type="button" 
                    className="remove-photo" 
                    onClick={(e) => {
                      e.stopPropagation();
                      setPhotoFile(null);
                      setError('');
                      document.getElementById('photo-input').value = '';
                    }}
                  >
                    ✕
                  </button>
                </div>
              ) : (
                <div className="photo-placeholder">
                  <div>
                    <svg 
                      width="40" 
                      height="40" 
                      viewBox="0 0 40 40" 
                      fill="none" 
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path d="M20 0L23.5 3.5H16.5L20 0Z" fill="#666"/>
                      <rect x="18" y="3" width="4" height="25" fill="#666"/>
                      <path d="M8 20L12 24V16L8 20Z" fill="#666"/>
                      <path d="M32 20L28 24V16L32 20Z" fill="#666"/>
                    </svg>
                    <span>Нажмите для загрузки фото</span>
                    <div className="photo-requirements">
                      Поддерживаются форматы: JPEG, PNG, GIF, WebP<br/>
                      Максимальный размер: 50 МБ
                    </div>
                  </div>
                </div>
              )}
              <input
                id="photo-input"
                type="file"
                accept="image/jpeg,image/png,image/gif,image/webp"
                onChange={handlePhotoChange}
                className="photo-input"
                onClick={(e) => e.stopPropagation()}
              />
            </div>
          </div>

          <div className="form-group">
            <label>Имя</label>
            <input
              type="text"
              value={profile.name}
              onChange={(e) => setProfile({...profile, name: e.target.value})}
              required
            />
          </div>

          <div className="form-group">
            <label>Возраст</label>
            <input
              type="number"
              min="18"
              max="100"
              value={profile.age}
              onChange={(e) => setProfile({...profile, age: e.target.value})}
              required
            />
          </div>

          <div className="form-group">
            <label>Пол</label>
            <select
              value={profile.gender}
              onChange={(e) => setProfile({...profile, gender: e.target.value})}
              required
            >
              <option value="">Выберите пол</option>
              <option value="female">Женский</option>
              <option value="male">Мужской</option>
            </select>
          </div>

          <div className="form-group">
            <label>Город</label>
            <input
              type="text"
              value={profile.city}
              onChange={(e) => setProfile({...profile, city: e.target.value})}
              required
              placeholder="Введите название города"
            />
          </div>

          <div className="form-group">
            <label>О себе</label>
            <textarea
              value={profile.about}
              onChange={(e) => setProfile({...profile, about: e.target.value})}
              rows="4"
            />
          </div>

          <div className="form-group">
            <label>Интересы (через запятую)</label>
            <input
              type="text"
              value={profile.interests}
              onChange={(e) => setProfile({...profile, interests: e.target.value})}
              placeholder="Например: спорт, музыка, путешествия"
            />
          </div>

          <div className="form-actions">
            <button 
              type="button" 
              onClick={() => navigate('/admin/profiles')} 
              className="cancel-button"
              disabled={isLoading}
            >
              Отмена
            </button>
            <button 
              type="submit" 
              className="submit-button"
              disabled={isLoading}
            >
              {isLoading ? 'Создание...' : 'Создать анкету'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AdminCreateProfilePage;
