import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './AdminCreateProfilePage.css';

const AdminCreateProfilePage = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    gender: '',
    city: '',
    about: '',
    interests: ''
  });
  const [photoFile, setPhotoFile] = useState(null);
  const [photoPreview, setPhotoPreview] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
        console.log('Выбран файл:', file.name, 'размер:', file.size, 'тип:', file.type);
        if (file.size > 50 * 1024 * 1024) {
            setError('Размер файла не должен превышать 50MB');
            return;
        }
        setPhotoFile(file);
        setPhotoPreview(URL.createObjectURL(file));
        setError('');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isLoading) return;

    setError('');
    setIsLoading(true);

    try {
      const token = localStorage.getItem('token');
      const formDataToSend = new FormData();
      
      Object.keys(formData).forEach(key => {
        formDataToSend.append(key, formData[key]);
      });

      if (photoFile) {
        formDataToSend.append('photo', photoFile);
      }

      await axios.post(
        'http://localhost:5000/api/profiles',
        formDataToSend,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      navigate('/admin/profiles');
    } catch (error) {
      console.error('Ошибка при создании анкеты:', error);
      setError(error.response?.data?.message || 'Ошибка при создании анкеты');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="admin-create-profile">
      <div className="create-profile-container">
        <h1>Создание новой анкеты</h1>
        
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Фото</label>
            <div className="photo-upload">
              <input
                type="file"
                accept="image/*"
                onChange={handlePhotoChange}
                className="photo-input"
                id="photo-upload"
              />
              <label htmlFor="photo-upload" className="photo-upload-label">
                {photoFile ? (
                  <div className="photo-preview-container">
                    <img 
                      src={URL.createObjectURL(photoFile)} 
                      alt="Preview" 
                      className="photo-preview" 
                    />
                    <button 
                      type="button" 
                      className="remove-photo"
                      onClick={(e) => {
                        e.preventDefault();
                        e.stopPropagation();
                        setPhotoFile(null);
                      }}
                    >
                      ✕
                    </button>
                  </div>
                ) : (
                  <div className="photo-placeholder">
                    <div>
                      <svg 
                        width="40" 
                        height="40" 
                        viewBox="0 0 40 40" 
                        fill="none" 
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path d="M20 0L23.5 3.5H16.5L20 0Z" fill="#666"/>
                        <rect x="18" y="3" width="4" height="25" fill="#666"/>
                        <path d="M8 20L12 24V16L8 20Z" fill="#666"/>
                        <path d="M32 20L28 24V16L32 20Z" fill="#666"/>
                      </svg>
                      <span>Нажмите для загрузки фото</span>
                    </div>
                  </div>
                )}
              </label>
            </div>
          </div>

          <div className="form-group">
            <label>Имя</label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({...formData, name: e.target.value})}
              required
            />
          </div>

          <div className="form-group">
            <label>Возраст</label>
            <input
              type="number"
              min="18"
              max="100"
              value={formData.age}
              onChange={(e) => setFormData({...formData, age: e.target.value})}
              required
            />
          </div>

          <div className="form-group">
            <label>Пол</label>
            <select
              value={formData.gender}
              onChange={(e) => setFormData({...formData, gender: e.target.value})}
              required
            >
              <option value="">Выберите пол</option>
              <option value="male">Мужской</option>
              <option value="female">Женский</option>
            </select>
          </div>

          <div className="form-group">
            <label>Город</label>
            <input
              type="text"
              value={formData.city}
              onChange={(e) => setFormData({...formData, city: e.target.value})}
              required
            />
          </div>

          <div className="form-group">
            <label>О себе</label>
            <textarea
              value={formData.about}
              onChange={(e) => setFormData({...formData, about: e.target.value})}
              rows="4"
            />
          </div>

          <div className="form-group">
            <label>Интересы</label>
            <input
              type="text"
              value={formData.interests}
              onChange={(e) => setFormData({...formData, interests: e.target.value})}
              placeholder="Например: спорт, музыка, путешествия"
            />
          </div>

          <div className="form-actions">
            <button 
              type="button" 
              onClick={() => navigate('/admin/profiles')}
              className="cancel-button"
              disabled={isLoading}
            >
              Отмена
            </button>
            <button 
              type="submit" 
              className="submit-button"
              disabled={isLoading}
            >
              {isLoading ? 'Создание...' : 'Создать анкету'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AdminCreateProfilePage;
