import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { getApiUrl } from '../config/api';
import SocialLinks from './SocialLinks';
import './ProfileCard.css';

const ProfileCard = ({ profile }) => {
  const [imageError, setImageError] = useState(false);
  const [settings, setSettings] = useState(null);
  
  useEffect(() => {
    const fetchSettings = async () => {
      try {
        const response = await axios.get(getApiUrl('/api/site-settings'));
        setSettings(response.data);
      } catch (error) {
        console.error('Ошибка при загрузке настроек:', error);
      }
    };

    fetchSettings();
  }, []);

  return (
    <div className="profile-card">
      <div className="profile-photo">
        {profile.photo && !imageError ? (
          <img 
            src={`${getApiUrl()}${profile.photo}`}
            alt={profile.name}
            onError={() => setImageError(true)}
          />
        ) : (
          <div className="profile-photo-placeholder">
            {profile.name ? profile.name.charAt(0) : '?'}
          </div>
        )}
      </div>
      
      <div className="profile-info">
        <h3 className="profile-name">{profile.name}</h3>
        <div className="profile-details">
          <span className="profile-age">{profile.age} лет</span>
          {profile.city && (
            <span className="profile-city">{profile.city}</span>
          )}
        </div>
        
        {profile.about && (
          <p className="profile-about">{profile.about}</p>
        )}
        
        {profile.interests && (
          <div className="profile-interests">
            {profile.interests.split(',').map((interest, index) => (
              <span key={index} className="interest-tag">
                {interest.trim()}
              </span>
            ))}
          </div>
        )}

        <SocialLinks profile={profile} settings={settings} />
      </div>
    </div>
  );
};

export default ProfileCard;
