import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProfileCard from '../components/ProfileCard';
import './ProfilesPage.css';

const ProfilesPage = () => {
  const [profiles, setProfiles] = useState([]);
  const [cities, setCities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentProfileIndex, setCurrentProfileIndex] = useState(0);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [filters, setFilters] = useState({
    city: '',
    gender: '',
    ageRange: '',
    interests: ''
  });

  useEffect(() => {
    Promise.all([
      fetchProfiles(),
      fetchCities()
    ]).finally(() => {
      setLoading(false);
    });
  }, []);

  const fetchProfiles = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/public/profiles');
      console.log('Загруженные анкеты:', response.data);
      setProfiles(response.data);
    } catch (error) {
      console.error('Ошибка при загрузке анкет:', error);
      setError('Ошибка при загрузке анкет');
    }
  };

  const fetchCities = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/cities/active');
      console.log('Загруженные города:', response.data);
      setCities(response.data);
    } catch (error) {
      console.error('Ошибка при загрузке городов:', error);
      setError('Ошибка при загрузке списка городов');
    }
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleFilterChange = (name, value) => {
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const resetFilters = () => {
    setFilters({
      city: '',
      gender: '',
      ageRange: '',
      interests: ''
    });
  };

  const filteredProfiles = profiles.filter(profile => {
    if (filters.city && filters.city !== 'all' && profile.city !== filters.city) {
      return false;
    }
    
    if (filters.gender && filters.gender !== 'all' && profile.gender !== filters.gender) {
      return false;
    }
    
    if (filters.ageRange && filters.ageRange !== 'all') {
      const [min, max] = filters.ageRange.split('-').map(Number);
      if (profile.age < min || (max && profile.age > max)) {
        return false;
      }
    }
    
    if (filters.interests) {
      const searchInterests = filters.interests.toLowerCase().split(',').map(i => i.trim());
      const profileInterests = profile.interests ? profile.interests.toLowerCase().split(',').map(i => i.trim()) : [];
      if (!searchInterests.some(interest => profileInterests.includes(interest))) {
        return false;
      }
    }
    
    return true;
  });

  const handleNextProfile = () => {
    if (currentProfileIndex < filteredProfiles.length - 1) {
      setCurrentProfileIndex(prev => prev + 1);
    }
  };

  const handlePrevProfile = () => {
    if (currentProfileIndex > 0) {
      setCurrentProfileIndex(prev => prev - 1);
    }
  };

  const isMobile = window.innerWidth <= 768;

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="profiles-page">
      <button className="toggle-sidebar-button" onClick={toggleSidebar}>
        {isSidebarOpen ? (
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M6 18L18 6M6 6L18 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
          </svg>
        ) : (
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M4 6H20M4 12H20M4 18H20" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
          </svg>
        )}
      </button>

      <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
        <div className="filters">
          <h3>Фильтры</h3>
          
          <div className="filter-group">
            <label>Город</label>
            <select 
              value={filters.city}
              onChange={(e) => handleFilterChange('city', e.target.value)}
            >
              <option value="all">Все города</option>
              {cities.map(city => (
                <option key={city._id} value={city.name}>
                  {city.name}
                </option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>Пол</label>
            <select
              value={filters.gender}
              onChange={(e) => handleFilterChange('gender', e.target.value)}
            >
              <option value="all">Любой</option>
              <option value="female">Женский</option>
              <option value="male">Мужской</option>
            </select>
          </div>

          <div className="filter-group">
            <label>Возраст</label>
            <select
              value={filters.ageRange}
              onChange={(e) => handleFilterChange('ageRange', e.target.value)}
            >
              <option value="all">Любой возраст</option>
              <option value="18-25">18-25 лет</option>
              <option value="26-35">26-35 лет</option>
              <option value="36-45">36-45 лет</option>
              <option value="46">46+ лет</option>
            </select>
          </div>

          <div className="filter-group">
            <label>Интересы</label>
            <input
              type="text"
              value={filters.interests}
              onChange={(e) => handleFilterChange('interests', e.target.value)}
              placeholder="Например: спорт, музыка"
            />
          </div>

          <button className="reset-filters" onClick={resetFilters}>
            Сбросить фильтры
          </button>
        </div>
      </div>

      <div className={`profiles-content ${isSidebarOpen ? 'sidebar-open' : ''}`}>
        {filteredProfiles.length === 0 ? (
          <div className="no-profiles">
            <p>Анкеты не найдены</p>
          </div>
        ) : (
          <>
            {isMobile ? (
              <div className="mobile-profile-view">
                <div className="profile-navigation">
                  <button 
                    onClick={handlePrevProfile}
                    disabled={currentProfileIndex === 0}
                  >
                    ←
                  </button>
                  <span>{currentProfileIndex + 1} из {filteredProfiles.length}</span>
                  <button 
                    onClick={handleNextProfile}
                    disabled={currentProfileIndex === filteredProfiles.length - 1}
                  >
                    →
                  </button>
                </div>
                <div className="single-profile">
                  <ProfileCard profile={filteredProfiles[currentProfileIndex]} />
                </div>
              </div>
            ) : (
              <div className="profiles-grid">
                {filteredProfiles.map(profile => (
                  <ProfileCard key={profile._id} profile={profile} />
                ))}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default ProfilesPage;
