import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import './Settings.css';

const Settings = () => {
  const { token } = useAuth();
  const [settings, setSettings] = useState({
    mainTitle: '',
    subtitle: '',
    description: '',
    telegramLink: '',
    whatsappLink: '',
    headerStyle: 'color'
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [headerImage, setHeaderImage] = useState(null);

  useEffect(() => {
    fetchSettings();
  }, []);

  const fetchSettings = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/settings');
      setSettings(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Ошибка при загрузке настроек:', error);
      setError('Ошибка при загрузке настроек');
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSettings(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    setHeaderImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    const formData = new FormData();
    Object.keys(settings).forEach(key => {
      formData.append(key, settings[key]);
    });
    
    if (headerImage) {
      formData.append('headerImage', headerImage);
    }

    try {
      await axios.put(
        'http://localhost:5000/api/settings',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        }
      );
      setSuccess('Настройки успешно сохранены');
    } catch (error) {
      console.error('Ошибка при сохранении настроек:', error);
      setError(error.response?.data?.message || 'Ошибка при сохранении настроек');
    }
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="settings">
      <h2>Настройки сайта</h2>
      
      <form onSubmit={handleSubmit} className="settings-form">
        <div className="form-group">
          <label>Главный заголовок</label>
          <input
            type="text"
            name="mainTitle"
            value={settings.mainTitle}
            onChange={handleInputChange}
            placeholder="Введите главный заголовок"
          />
        </div>

        <div className="form-group">
          <label>Подзаголовок</label>
          <input
            type="text"
            name="subtitle"
            value={settings.subtitle}
            onChange={handleInputChange}
            placeholder="Введите подзаголовок"
          />
        </div>

        <div className="form-group">
          <label>Описание</label>
          <textarea
            name="description"
            value={settings.description}
            onChange={handleInputChange}
            placeholder="Введите описание сайта"
          />
        </div>

        <div className="form-group">
          <label>Ссылка на Telegram</label>
          <input
            type="text"
            name="telegramLink"
            value={settings.telegramLink}
            onChange={handleInputChange}
            placeholder="Введите ссылку на Telegram"
          />
        </div>

        <div className="form-group">
          <label>Ссылка на WhatsApp</label>
          <input
            type="text"
            name="whatsappLink"
            value={settings.whatsappLink}
            onChange={handleInputChange}
            placeholder="Введите ссылку на WhatsApp"
          />
        </div>

        <div className="form-group">
          <label>Тип фона шапки</label>
          <div className="radio-group">
            <label>
              <input
                type="radio"
                name="headerStyle"
                value="color"
                checked={settings.headerStyle === 'color'}
                onChange={handleInputChange}
              />
              Цвет
            </label>
            <label>
              <input
                type="radio"
                name="headerStyle"
                value="image"
                checked={settings.headerStyle === 'image'}
                onChange={handleInputChange}
              />
              Изображение
            </label>
          </div>
        </div>

        {settings.headerStyle === 'image' && (
          <div className="form-group">
            <label>Фоновое изображение</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
            />
          </div>
        )}

        {success && <div className="success-message">{success}</div>}
        {error && <div className="error-message">{error}</div>}

        <button type="submit" className="save-button">
          Сохранить настройки
        </button>
      </form>
    </div>
  );
};

export default Settings;
