import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { getApiUrl, getMediaUrl } from '../config/api';

const BackgroundProvider = ({ children }) => {
    const [settings, setSettings] = useState(null);

    const updateBackground = (data) => {
        console.log('Обновление фона с данными:', data);
        
        // Применяем фон сайта
        if (data.siteBackground === 'image' && data.siteBackgroundImage) {
            const imageUrl = getMediaUrl(data.siteBackgroundImage);
            console.log('Применяем фоновое изображение сайта:', imageUrl);
            
            document.body.style.backgroundImage = `url(${imageUrl})`;
            document.body.style.backgroundSize = 'cover';
            document.body.style.backgroundPosition = 'center';
            document.body.style.backgroundAttachment = 'fixed';
            document.body.style.backgroundRepeat = 'no-repeat';
            document.body.style.backgroundColor = 'transparent';
        } else {
            console.log('Применяем цветовой фон сайта:', data.siteBackgroundColor);
            document.body.style.backgroundImage = 'none';
            document.body.style.backgroundColor = data.siteBackgroundColor || '#f5f5f5';
        }

        // Получаем элемент шапки
        const headerElement = document.querySelector('.navigation');
        if (headerElement) {
            // Применяем фон шапки
            if (data.headerBackground === 'image' && data.headerBackgroundImage) {
                const headerImageUrl = getMediaUrl(data.headerBackgroundImage);
                console.log('Применяем фоновое изображение шапки:', headerImageUrl);
                
                headerElement.style.backgroundImage = `url(${headerImageUrl})`;
                headerElement.style.backgroundSize = 'cover';
                headerElement.style.backgroundPosition = 'center';
                headerElement.style.backgroundColor = 'transparent';
            } else {
                console.log('Применяем цветовой фон шапки:', data.headerBackgroundColor);
                headerElement.style.backgroundImage = 'none';
                headerElement.style.backgroundColor = data.headerBackgroundColor || '#ffffff';
            }
        }

        console.log('Применены настройки фона:', {
            siteBackground: {
                type: data.siteBackground,
                image: data.siteBackgroundImage,
                color: data.siteBackgroundColor
            },
            headerBackground: {
                type: data.headerBackground,
                image: data.headerBackgroundImage,
                color: data.headerBackgroundColor
            }
        });
    };

    useEffect(() => {
        const fetchSettings = async () => {
            try {
                console.log('Загрузка настроек фона...');
                const response = await axios.get(getApiUrl('/api/site-settings'));
                console.log('Получены настройки:', response.data);
                setSettings(response.data);
                updateBackground(response.data);
            } catch (error) {
                console.error('Ошибка при загрузке настроек фона:', error);
                // Устанавливаем фон по умолчанию
                document.body.style.backgroundImage = 'none';
                document.body.style.backgroundColor = '#f5f5f5';
                const headerElement = document.querySelector('.navigation');
                if (headerElement) {
                    headerElement.style.backgroundImage = 'none';
                    headerElement.style.backgroundColor = '#ffffff';
                }
            }
        };

        const handleSettingsUpdate = () => {
            console.log('Получено событие обновления настроек');
            fetchSettings();
        };

        // Подписываемся на событие обновления настроек
        window.addEventListener('settingsUpdated', handleSettingsUpdate);

        // Загружаем настройки при монтировании
        fetchSettings();

        // Очистка при размонтировании
        return () => {
            document.body.style.backgroundImage = 'none';
            document.body.style.backgroundColor = '#f5f5f5';
            const headerElement = document.querySelector('.navigation');
            if (headerElement) {
                headerElement.style.backgroundImage = 'none';
                headerElement.style.backgroundColor = '#ffffff';
            }
            window.removeEventListener('settingsUpdated', handleSettingsUpdate);
        };
    }, []);

    return <>{children}</>;
};

export default BackgroundProvider;
