import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminSettingsPage.css';

const AdminSettingsPage = () => {
  const [settings, setSettings] = useState({
    mainTitle: '',
    mainSubtitle: '',
    loginPageTitle: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);

  useEffect(() => {
    fetchSettings();
  }, []);

  const fetchSettings = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/site-settings');
      setSettings(response.data);
    } catch (error) {
      console.error('Ошибка при загрузке настроек:', error);
      setError('Ошибка при загрузке настроек сайта');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isSaving) return;

    setError('');
    setSuccess('');
    setIsSaving(true);

    try {
      const token = localStorage.getItem('token');
      await axios.put(
        'http://localhost:5000/api/site-settings',
        settings,
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );
      setSuccess('Настройки успешно сохранены');
    } catch (error) {
      console.error('Ошибка при сохранении настроек:', error);
      setError(error.response?.data?.message || 'Ошибка при сохранении настроек');
    } finally {
      setIsSaving(false);
    }
  };

  if (isLoading) {
    return <div className="loading">Загрузка настроек...</div>;
  }

  return (
    <div className="admin-settings">
      <div className="settings-container">
        <h1>Настройки сайта</h1>
        
        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Заголовок главной страницы</label>
            <input
              type="text"
              value={settings.mainTitle}
              onChange={(e) => setSettings({...settings, mainTitle: e.target.value})}
              required
            />
          </div>

          <div className="form-group">
            <label>Подзаголовок главной страницы</label>
            <input
              type="text"
              value={settings.mainSubtitle}
              onChange={(e) => setSettings({...settings, mainSubtitle: e.target.value})}
              required
            />
          </div>

          <div className="form-group">
            <label>Заголовок страницы входа</label>
            <input
              type="text"
              value={settings.loginPageTitle}
              onChange={(e) => setSettings({...settings, loginPageTitle: e.target.value})}
              required
            />
          </div>

          <button 
            type="submit" 
            className="save-button"
            disabled={isSaving}
          >
            {isSaving ? 'Сохранение...' : 'Сохранить настройки'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default AdminSettingsPage;
