import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import logger from '../utils/logger';
import './Navigation.css';

const Navigation = () => {
  const { isAuthenticated, user, logout, isAdmin } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logger.info('Пользователь выходит из системы', { userId: user?.id });
    logout();
  };

  return (
    <nav className="navigation">
      <div className="nav-brand">
        <Link to="/">Сайт знакомств</Link>
      </div>
      <div className="nav-links">
        {/* Базовые ссылки, доступные всем */}
        <Link to="/profiles" className="nav-link">Анкеты</Link>
        <Link to="/search" className="nav-link">Поиск</Link>

        {/* Условный рендеринг для авторизованных пользователей */}
        {isAuthenticated ? (
          <>
            {isAdmin ? (
              // Меню администратора
              <>
                <Link to="/admin/profiles" className="nav-link">Управление анкетами</Link>
                <Link to="/admin/create" className="nav-link">Создать анкету</Link>
                <Link to="/admin/settings" className="nav-link">Настройки сайта</Link>
              </>
            ) : (
              // Меню обычного пользователя
              <Link to="/cabinet" className="nav-link">Личный кабинет</Link>
            )}
            <button 
              onClick={handleLogout} 
              className="nav-link logout-button"
            >
              Выйти
            </button>
          </>
        ) : (
          // Ссылки для неавторизованных пользователей
          <>
            <Link to="/login" className="nav-link">Вход</Link>
            <Link to="/register" className="nav-link">Регистрация</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navigation;
