import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import './Settings.css';

const Settings = () => {
  const { token } = useAuth();
  const [settings, setSettings] = useState({
    telegramLink: '',
    whatsappLink: ''
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    fetchSettings();
  }, []);

  const fetchSettings = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/site-settings');
      setSettings(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Ошибка при загрузке настроек:', error);
      setError('Ошибка при загрузке настроек');
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSettings(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      await axios.put(
        'http://localhost:5000/api/site-settings',
        settings,
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );
      setSuccess('Настройки успешно сохранены');
    } catch (error) {
      console.error('Ошибка при сохранении настроек:', error);
      setError(error.response?.data?.message || 'Ошибка при сохранении настроек');
    }
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="settings">
      <h2>Настройки мессенджеров</h2>
      
      <form onSubmit={handleSubmit} className="settings-form">
        <div className="form-group">
          <label>Ссылка на Telegram</label>
          <input
            type="text"
            name="telegramLink"
            value={settings.telegramLink || ''}
            onChange={handleInputChange}
            placeholder="https://t.me/username"
          />
        </div>

        <div className="form-group">
          <label>Ссылка на WhatsApp</label>
          <input
            type="text"
            name="whatsappLink"
            value={settings.whatsappLink || ''}
            onChange={handleInputChange}
            placeholder="https://wa.me/1234567890"
          />
        </div>

        {success && <div className="success-message">{success}</div>}
        {error && <div className="error-message">{error}</div>}

        <button type="submit" className="save-button">
          Сохранить настройки
        </button>
      </form>
    </div>
  );
};

export default Settings;
