import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import './ProfileList.css';

const ProfileList = () => {
  const { token } = useAuth();
  const [profiles, setProfiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchProfiles();
  }, []);

  const fetchProfiles = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/profiles', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setProfiles(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Ошибка при загрузке анкет:', error);
      setError('Ошибка при загрузке анкет');
      setLoading(false);
    }
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="profile-list">
      <h2>Управление анкетами</h2>
      <div className="profiles-grid">
        {profiles.map(profile => (
          <div key={profile._id} className="profile-card">
            <div className="profile-image">
              {profile.photo ? (
                <img src={`http://localhost:5000${profile.photo}`} alt={profile.name} />
              ) : (
                <div className="no-photo">Нет фото</div>
              )}
            </div>
            <div className="profile-info">
              <h3>{profile.name}</h3>
              <p>Возраст: {profile.age}</p>
              <p>Статус: {profile.status}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProfileList;
