import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Navigation.css';

const Navigation = () => {
  const { isAuthenticated, isAdmin, logout } = useAuth();
  const [isMenuVisible, setIsMenuVisible] = useState(true);
  const [isAdminMenuOpen, setIsAdminMenuOpen] = useState(false);
  const location = useLocation();

  // Проверяем, находимся ли мы в админ-панели
  const isAdminPage = location.pathname.startsWith('/admin');

  // Обработка скролла
  useEffect(() => {
    let lastScrollY = window.scrollY;

    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      // Показываем меню при скролле вверх или в самом верху страницы
      if (currentScrollY < lastScrollY || currentScrollY < 50) {
        setIsMenuVisible(true);
      } else {
        setIsMenuVisible(false);
      }

      lastScrollY = currentScrollY;
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleAdminMenu = () => {
    setIsAdminMenuOpen(!isAdminMenuOpen);
  };

  const handleLogout = () => {
    logout();
    setIsAdminMenuOpen(false);
  };

  // Если это админ-страница, показываем только меню-гамбургер
  if (isAdmin && isAdminPage) {
    return (
      <nav className={`navigation admin-only ${!isMenuVisible ? 'hidden' : ''}`}>
        <div className="nav-content admin-only">
          <div className="nav-right">
            {!isAuthenticated && (
              <Link to="/login" className="login-button">
                Вход для рекламодателей
              </Link>
            )}
            {isAdmin && (
              <div className="admin-menu-container">
                <button className="admin-menu-toggle" onClick={toggleAdminMenu}>
                  {isAdminMenuOpen ? (
                    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M6 18L18 6M6 6L18 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
                    </svg>
                  ) : (
                    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M4 6H20M4 12H20M4 18H20" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
                    </svg>
                  )}
                  <span>Админ-панель</span>
                </button>

                <div className={`admin-dropdown ${isAdminMenuOpen ? 'open' : ''}`}>
                  <Link 
                    to="/admin/profiles" 
                    className={location.pathname === '/admin/profiles' ? 'active' : ''}
                    onClick={() => setIsAdminMenuOpen(false)}
                  >
                    Управление анкетами
                  </Link>
                  <Link 
                    to="/admin/cities" 
                    className={location.pathname === '/admin/cities' ? 'active' : ''}
                    onClick={() => setIsAdminMenuOpen(false)}
                  >
                    Управление городами
                  </Link>
                  <Link 
                    to="/admin/create" 
                    className={location.pathname === '/admin/create' ? 'active' : ''}
                    onClick={() => setIsAdminMenuOpen(false)}
                  >
                    Создать анкету
                  </Link>
                  <Link 
                    to="/admin/settings" 
                    className={location.pathname === '/admin/settings' ? 'active' : ''}
                    onClick={() => setIsAdminMenuOpen(false)}
                  >
                    Настройки сайта
                  </Link>
                  <button className="logout-button" onClick={handleLogout}>
                    Выйти
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </nav>
    );
  }

  // Для остальных страниц показываем обычную навигацию
  return (
    <nav className={`navigation ${!isMenuVisible ? 'hidden' : ''}`}>
      <div className="nav-content">
        <div className="nav-left">
          <Link to="/" className="nav-brand">
            Сайт знакомств
          </Link>
        </div>

        <div className="nav-links">
          <Link to="/profiles" className={location.pathname === '/profiles' ? 'active' : ''}>
            Анкеты
          </Link>
          <Link to="/search" className={location.pathname === '/search' ? 'active' : ''}>
            Поиск
          </Link>

          {!isAuthenticated ? (
            <div className="auth-buttons">
              <Link to="/login" className={`login-button ${location.pathname === '/login' ? 'active' : ''}`}>
                Вход для рекламодателей
              </Link>
            </div>
          ) : null}
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
