// Базовый URL для API
export const API_BASE_URL = 'http://localhost:3000';

// Функция для формирования полного URL API
export const getApiUrl = (endpoint) => endpoint;

// Функция для формирования полного URL для медиа-файлов
export const getMediaUrl = (path) => path ? path : null;

// Настройка axios по умолчанию
import axios from 'axios';

// Устанавливаем базовый URL
axios.defaults.baseURL = '';  // Используем относительные пути для прокси

// Добавляем перехватчик для обработки ошибок
axios.interceptors.response.use(
    response => response,
    error => {
        console.error('API Error:', error);
        if (error.response) {
            console.error('Response data:', error.response.data);
            console.error('Response status:', error.response.status);
        }
        return Promise.reject(error);
    }
);
