import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './AdminProfilesPage.css';

const AdminProfilesPage = () => {
  const [profiles] = useState([
    {
      id: 1,
      name: 'Анна',
      age: 25,
      city: 'Москва',
      status: 'active'
    },
    {
      id: 2,
      name: 'Александр',
      age: 28,
      city: 'Санкт-Петербург',
      status: 'pending'
    }
  ]);

  const handleStatusChange = (id, newStatus) => {
    // Здесь будет логика обновления статуса через API
    console.log(`Изменение статуса анкеты ${id} на ${newStatus}`);
  };

  const handleDelete = (id) => {
    // Здесь будет логика удаления через API
    if (window.confirm('Вы уверены, что хотите удалить эту анкету?')) {
      console.log(`Удаление анкеты ${id}`);
    }
  };

  return (
    <div className="admin-profiles-page">
      <div className="admin-header">
        <h1>Управление анкетами</h1>
        <Link to="/admin/create" className="create-button">
          Создать анкету
        </Link>
      </div>

      <div className="profiles-table">
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Имя</th>
              <th>Возраст</th>
              <th>Город</th>
              <th>Статус</th>
              <th>Действия</th>
            </tr>
          </thead>
          <tbody>
            {profiles.map(profile => (
              <tr key={profile.id}>
                <td>{profile.id}</td>
                <td>{profile.name}</td>
                <td>{profile.age}</td>
                <td>{profile.city}</td>
                <td>
                  <select
                    value={profile.status}
                    onChange={(e) => handleStatusChange(profile.id, e.target.value)}
                    className={`status-select status-${profile.status}`}
                  >
                    <option value="active">Активна</option>
                    <option value="pending">На модерации</option>
                    <option value="blocked">Заблокирована</option>
                  </select>
                </td>
                <td className="actions">
                  <Link to={`/admin/edit/${profile.id}`} className="edit-button">
                    Редактировать
                  </Link>
                  <button
                    onClick={() => handleDelete(profile.id)}
                    className="delete-button"
                  >
                    Удалить
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminProfilesPage;
