import React, { createContext, useContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import logger from '../utils/logger';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    // Проверяем токен при загрузке
    useEffect(() => {
        const initAuth = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    logger.info('Токен не найден');
                    setLoading(false);
                    return;
                }

                // Устанавливаем токен для всех последующих запросов
                axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
                
                const response = await axios.get('http://localhost:5000/api/auth/me');
                setUser(response.data);
                logger.info('Пользователь авторизован', { 
                    userId: response.data.id,
                    role: response.data.role 
                });
            } catch (error) {
                logger.error('Ошибка при проверке токена', error);
                localStorage.removeItem('token');
                delete axios.defaults.headers.common['Authorization'];
                setUser(null);
            } finally {
                setLoading(false);
            }
        };

        initAuth();
    }, []);

    const login = async (token, userData) => {
        try {
            localStorage.setItem('token', token);
            axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            setUser(userData);
            logger.info('Пользователь вошел в систему', { 
                userId: userData.id,
                role: userData.role 
            });

            // Перенаправляем в зависимости от роли
            if (userData.role === 'admin') {
                navigate('/admin/profiles');
            } else {
                navigate('/cabinet');
            }
        } catch (error) {
            logger.error('Ошибка при входе', error);
            throw error;
        }
    };

    const logout = () => {
        try {
            localStorage.removeItem('token');
            delete axios.defaults.headers.common['Authorization'];
            setUser(null);
            logger.info('Пользователь вышел из системы');
            navigate('/login');
        } catch (error) {
            logger.error('Ошибка при выходе', error);
        }
    };

    // Показываем индикатор загрузки только при первичной инициализации
    if (loading) {
        return (
            <div className="auth-loading">
                <div className="loading-spinner">Загрузка...</div>
            </div>
        );
    }

    const value = {
        user,
        isAuthenticated: !!user,
        login,
        logout,
        isAdmin: user?.role === 'admin',
        loading
    };

    return (
        <AuthContext.Provider value={value}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth должен использоваться внутри AuthProvider');
    }
    return context;
};
