import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { getApiUrl, getMediaUrl } from '../config/api';

const BackgroundProvider = ({ children }) => {
    const [settings, setSettings] = useState(null);

    const updateBackground = (data) => {
        // Базовые стили для контейнера
        document.documentElement.style.height = '100%';
        document.body.style.margin = '0';
        document.body.style.padding = '0';
        document.body.style.minHeight = '100vh';
        document.body.style.width = '100%';
        document.body.style.overflowX = 'hidden';
        
        if (data.siteBackground === 'image' && data.siteBackgroundImage) {
            const imageUrl = getMediaUrl(data.siteBackgroundImage);
            
            // Стили для фонового изображения
            document.body.style.backgroundImage = `url(${imageUrl})`;
            document.body.style.backgroundSize = 'contain';
            document.body.style.backgroundPosition = 'top center';
            document.body.style.backgroundRepeat = 'no-repeat';
            document.body.style.backgroundColor = '#f5f5f5';
            
            // Создаем или обновляем контейнер для контента
            let contentContainer = document.getElementById('content-container');
            if (!contentContainer) {
                contentContainer = document.createElement('div');
                contentContainer.id = 'content-container';
                // Перемещаем все дочерние элементы body в контейнер
                while (document.body.firstChild) {
                    contentContainer.appendChild(document.body.firstChild);
                }
                document.body.appendChild(contentContainer);
            }
            
            // Стили для контейнера контента
            contentContainer.style.position = 'relative';
            contentContainer.style.zIndex = '1';
            contentContainer.style.width = '100%';
            contentContainer.style.maxWidth = '1200px';
            contentContainer.style.margin = '0 auto';
            contentContainer.style.padding = '20px';
            contentContainer.style.boxSizing = 'border-box';
            
        } else {
            document.body.style.backgroundImage = 'none';
            document.body.style.backgroundColor = data.siteBackgroundColor || '#f5f5f5';
            
            // Возвращаем контент обратно в body если есть контейнер
            const contentContainer = document.getElementById('content-container');
            if (contentContainer) {
                while (contentContainer.firstChild) {
                    document.body.appendChild(contentContainer.firstChild);
                }
                contentContainer.remove();
            }
        }
    };

    useEffect(() => {
        const fetchSettings = async () => {
            try {
                const response = await axios.get(getApiUrl('/api/site-settings'));
                setSettings(response.data);
                updateBackground(response.data);
            } catch (error) {
                console.error('Ошибка при загрузке настроек фона:', error);
                document.body.style.backgroundImage = 'none';
                document.body.style.backgroundColor = '#f5f5f5';
            }
        };

        const handleSettingsUpdate = () => {
            fetchSettings();
        };

        fetchSettings();
        window.addEventListener('settingsUpdated', handleSettingsUpdate);

        return () => {
            window.removeEventListener('settingsUpdated', handleSettingsUpdate);
            const contentContainer = document.getElementById('content-container');
            if (contentContainer) {
                while (contentContainer.firstChild) {
                    document.body.appendChild(contentContainer.firstChild);
                }
                contentContainer.remove();
            }
            document.body.style = '';
            document.documentElement.style = '';
        };
    }, []);

    return <>{children}</>;
};

export default BackgroundProvider;
