import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './HomePage.css';

const HomePage = () => {
  const [settings, setSettings] = useState({
    mainTitle: 'Сайт знакомств',
    mainSubtitle: 'Найдите свою любовь в нашем городе'
  });
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchSettings = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/site-settings');
        if (response.data) {
          setSettings(response.data);
        }
      } catch (error) {
        console.error('Ошибка при загрузке настроек:', error);
        // В случае ошибки оставляем дефолтные значения
      } finally {
        setIsLoading(false);
      }
    };
    fetchSettings();
  }, []);

  if (isLoading) {
    return (
      <div className="home-page">
        <section className="hero">
          <h1>{settings.mainTitle}</h1>
          <p>{settings.mainSubtitle}</p>
        </section>
      </div>
    );
  }

  return (
    <div className="home-page">
      <section className="hero">
        <h1>{settings.mainTitle}</h1>
        <p>{settings.mainSubtitle}</p>
        <Link to="/profiles" className="cta-button">
          Смотреть анкеты
        </Link>
      </section>
      
      <section className="features">
        <div className="feature-card">
          <h3>Простой поиск</h3>
          <p>Используйте фильтры для поиска подходящих людей</p>
        </div>
        <div className="feature-card">
          <h3>Подробные анкеты</h3>
          <p>Узнайте больше о человеке перед знакомством</p>
        </div>
        <div className="feature-card">
          <h3>Безопасность</h3>
          <p>Все анкеты проверяются администрацией</p>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
