import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import './CityList.css';

const CityList = () => {
  const { token } = useAuth();
  const [cities, setCities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [newCityName, setNewCityName] = useState('');

  useEffect(() => {
    fetchCities();
  }, []);

  const fetchCities = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/cities');
      setCities(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Ошибка при загрузке городов:', error);
      setError('Ошибка при загрузке списка городов');
      setLoading(false);
    }
  };

  const handleAddCity = async (e) => {
    e.preventDefault();
    if (!newCityName.trim()) return;

    try {
      const response = await axios.post(
        'http://localhost:5000/api/cities',
        { name: newCityName.trim() },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setCities([...cities, response.data]);
      setNewCityName('');
    } catch (error) {
      console.error('Ошибка при добавлении города:', error);
      setError(error.response?.data?.message || 'Ошибка при добавлении города');
    }
  };

  const handleStatusChange = async (id, isActive) => {
    try {
      await axios.patch(
        `http://localhost:5000/api/cities/${id}/status`,
        { isActive },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setCities(cities.map(city => 
        city._id === id ? { ...city, isActive } : city
      ));
    } catch (error) {
      console.error('Ошибка при обновлении статуса города:', error);
      setError('Ошибка при обновлении статуса города');
    }
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="city-list">
      <h2>Управление городами</h2>
      
      <form className="add-city-form" onSubmit={handleAddCity}>
        <input
          type="text"
          value={newCityName}
          onChange={(e) => setNewCityName(e.target.value)}
          placeholder="Введите название города"
          required
        />
        <button type="submit">Добавить город</button>
      </form>

      <div className="cities-grid">
        {cities.map(city => (
          <div key={city._id} className="city-card">
            <h3>{city.name}</h3>
            <div className="city-status">
              <label className="switch">
                <input
                  type="checkbox"
                  checked={city.isActive}
                  onChange={(e) => handleStatusChange(city._id, e.target.checked)}
                />
                <span className="slider"></span>
              </label>
              <span>{city.isActive ? 'Активен' : 'Неактивен'}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CityList;
