import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { getApiUrl } from '../config/api';

const BackgroundProvider = ({ children }) => {
    const [settings, setSettings] = useState(null);

    useEffect(() => {
        const fetchSettings = async () => {
            try {
                const response = await axios.get(getApiUrl('/api/site-settings'));
                setSettings(response.data);
                
                // Применяем настройки фона
                if (response.data.siteBackground === 'image' && response.data.siteBackgroundImage) {
                    document.body.style.backgroundImage = `url(${getApiUrl()}${response.data.siteBackgroundImage})`;
                    document.body.style.backgroundSize = 'cover';
                    document.body.style.backgroundPosition = 'center';
                    document.body.style.backgroundAttachment = 'fixed';
                    document.body.style.backgroundColor = '#f5f5f5'; // Фоновый цвет на случай, пока грузится изображение
                } else {
                    document.body.style.backgroundImage = 'none';
                    document.body.style.backgroundColor = response.data.siteBackgroundColor || '#f5f5f5';
                }
            } catch (error) {
                console.error('Ошибка при загрузке настроек фона:', error);
            }
        };

        fetchSettings();

        // Очистка при размонтировании
        return () => {
            document.body.style.backgroundImage = 'none';
            document.body.style.backgroundColor = '#f5f5f5';
        };
    }, []);

    return <>{children}</>;
};

export default BackgroundProvider;
